import React, { useEffect, useState } from 'react'
import {over} from 'stompjs';
import SockJS from 'sockjs-client';

var stompClient = null;
const Lobby = () => {

    const connected = false;
    const [userData, setUserData] = useState({
        username: '',
        connected: false,
        lobbyCode: ''
    });

    const connect =()=>{
        let Sock = new SockJS('http://localhost:8080/ws');
        stompClient = over(Sock);
        stompClient.connect({},onConnected, onError);

    }

    const generateCode = () => {
        const min = 100000;
        const max = 999999;
        const randomNumber = Math.floor(Math.random() * (max - min + 1)) + min;
        return randomNumber;
    }

    const onConnected = () => {
        setUserData({...userData,"connected": true, "lobbyCode": generateCode()});
    }

    const onError = (err) => {
        console.log(err);

    }

    const registerUser =()=> {
        connect();
    }

    return (
        <div className ="container">
            {userData.connected?
            <div className = "lobby">
                <p>
                    {userData.lobbyCode}
                </p>
            </div>
            :
            <div className = "login">


                <div id="main">
                    <div id="title-block">
                        <h1>The Crooked Pub</h1>
                    </div>
                    <section id="button-block">
                        <button type="button" onClick={registerUser}>
                            Create New Game
                        </button>

                        <button type="button"> 
                            Join Game
                        </button>
                    </section>
                    <div id="how-to-play">
                        <p>How to play:</p>
                        <p>Instructions...</p>
                    </div>
                </div>
            </div>
            }
        </div>
  )

}

export default Lobby
