import React, { useEffect, useState } from 'react'
import {over} from 'stompjs';
import SockJS from 'sockjs-client';

var stompClient = null;
const Lobby = () => {
    const [userData, setUserData] = useState({
        username: '',
        connected: false,
        lobbyCode: ''
    });

    const connect =()=>{
        let Sock = new SockJS('http://localhost:8080/ws');
        stompClient = over(Sock);
    }

    const joinGame = () => {
        connect();
        stompClient.connect({},onJoin, onError);
    }

    const createGame =()=> {
        connect();
        stompClient.connect({},onCreate, onError);
    }

    const generateCode = () => {
        return new Promise((resolve) => {
            const min = 100000;
            const max = 999999;
            const randomNumber = Math.floor(Math.random() * (max - min + 1)) + min;
            const codeAsString = randomNumber.toString(); // Convert the number to a string
            resolve(codeAsString);
        });
    };

    const onCreate = async () => {
        const newLobbyCode = await generateCode();

        setUserData({...userData, "connected": true, "lobbyCode": newLobbyCode});

        const lobby = {
            lobbyCode: newLobbyCode,
            players: [userData.username]
        };

        stompClient.send('/app/lobby/create', {}, JSON.stringify(lobby));
        stompClient.subscribe('/topic/lobbyState/'+newLobbyCode);

        return userData; // Return the updated userData after setting lobbyCode
    };


    const onJoin = () => {
        setUserData({...userData,"connected": true});
        const lobby = {
            lobbyCode: userData.lobbyCode,
            players: [userData.username]
        };

        stompClient.send('/app/lobby/join', {}, JSON.stringify(lobby));
        stompClient.subscribe('/topic/lobbyState/'+userData.lobbyCode);
    }

    const onError = (err) => {
        console.log(err);
    }



    return (
        <div className ="container">
            {userData.connected?
            <div className = "lobby">
                <p>
                    {userData.lobbyCode}
                    {userData.username}
                </p>
            </div>
            :
            <div className = "login">


                <div id="main">
                    <div id="title-block">
                        <h1>The Crooked Pub</h1>
                    </div>
                    <div id="button-block">
                        <input
                            type="text"
                            placeholder="Enter your name"
                            value={userData.username}
                            onChange={(e) => setUserData({...userData,"username": (e.target.value)})}
                        />
                        <input
                            type="text"
                            placeholder="Enter lobby code"
                            value={userData.lobbyCode}
                            onChange={(e) => setUserData({...userData,"lobbyCode": (e.target.value)})}
                        />
                        <button type="button" onClick={createGame}>
                            Create New Game
                        </button>
                        <button type="button" onClick={joinGame}>
                            Join Game
                        </button>
                    </div>

                    <div id="how-to-play">
                        <p>How to play:</p>
                        <p>Instructions...</p>
                    </div>
                </div>
            </div>
            }
        </div> 
  )
}

export default Lobby
