import React, { useEffect, useState } from 'react'
import {over} from 'stompjs';
import SockJS from 'sockjs-client';

var stompClient = null;
const Lobby = () => {
    const [userData, setUserData] = useState({
        userId: '',
        username:'',
        connected: false,
        lobbyCode: '',
        created: false
    })

    // const [lobbyState, setLobbyState] = useState({
    //     lobbyCode: '',
    //     players: '',
    //     playerIds: '',
    //     answers: ''
    // });


    const connect =()=>{
        let Sock = new SockJS('http://localhost:8080/ws');
        stompClient = over(Sock);
        stompClient.connect({},onConnected, onError);
    }

    const onConnected = async () => {
        setUserData({...userData, "connected": true});
        const newUserId = await generateUserID();
        setUserData({...userData, "userId": newUserId})
        if (userData.created === true) {
            const newLobbyCode = await generateCode();
            setUserData({...userData, "lobbyCode": newLobbyCode})
        }
        stompClient.subscribe('/user/' + userData.lobbyCode + '/private', onMessageReceived);

        const lobby = {
            lobbyCode: userData.lobbyCode,
            players: [userData.username],
            playerIds: [userData.userId]
        };
        if (userData.created === true) {
            stompClient.send('/app/lobby/create', {}, JSON.stringify(lobby));
        } else {
            stompClient.send('/app/lobby/join', {}, JSON.stringify(lobby));
        }
    }

    const joinGame = () => {
        connect();
    }

    const createGame =()=> {
        setUserData({...userData, "created": true})
        console.log(userData.created)
        connect();
    }

    const generateCode = () => {
        return new Promise((resolve) => {
            const min = 100000;
            const max = 999999;
            const randomNumber = Math.floor(Math.random() * (max - min + 1)) + min;
            const codeAsString = randomNumber.toString(); // Convert the number to a string
            resolve(codeAsString);
        });
    };

    const generateUserID = () => {

        return new Promise((resolve) => {
            const userId = 'user-' + Math.random().toString(36).substr(2, 9);
            resolve(userId);
        });
    };

    const onError = (err) => {
        console.log(err);
    }

    const onMessageReceived = (payload) => {
        console.log(payload)
    }

    const updateGame = () => {
        const lobby = {
            lobbyCode: userData.lobbyCode,
            answers: [[1,2,3]]
        }
        stompClient.send('/app/lobby/update', {}, JSON.stringify(lobby));
    }



    return (
        <div className ="container">
            {userData.connected?
            <div className = "lobby">
                <p>
                    {userData.lobbyCode}
                    {userData.username}
                </p>
                <button type="button" onClick={updateGame}>
                            Update Game
                </button>
            </div>
            :
            <div className = "login">


                <div id="main">
                    <div id="title-block">
                        <h1>The Crooked Pub</h1>
                    </div>
                    <div id="button-block">
                        <input
                            type="text"
                            placeholder="Enter your name"
                            value={userData.username}
                            onChange={(e) => setUserData({...userData, "username": e.target.value}) }
                        />
                        <input
                            type="text"
                            placeholder="Enter lobby code"
                            value={userData.lobbyCode}
                            onChange={(e) => setUserData({...userData, "lobbyCode": e.target.value})}
                        />
                        <button type="button" onClick={createGame}>
                            Create New Game
                        </button>
                        <button type="button" onClick={joinGame}>
                            Join Game
                        </button>
                    </div>

                    <div id="how-to-play">
                        <p>How to play:</p>
                        <p>Instructions...</p>
                    </div>
                </div>
            </div>
            }
        </div> 
  )
}

export default Lobby
