import React, { useEffect, useState } from 'react'
import {over} from 'stompjs';
import SockJS from 'sockjs-client';

var stompClient = null;
const Lobby = () => {
    const [userData, setUserData] = useState({
        userId: '',
        username:'',
        lobbyCode: '',
        connected: false
    })
    const predefinedQuestions = [
        "Question 1",
        "Question 2",
        "Question 3",
        "Question 4",
        "Question 5",
        "Question 6",
        "Question 7",
        "Question 8",
        "Question 9",
        "Question 10"
    ];

    const lobbyMembers = ["User1", "User2", "User3"];

    const connect = ()=>{
        let Sock = new SockJS('http://localhost:8080/ws');
        stompClient = over(Sock);
    }

    const onCreate = async () => {
        const newLobbyCode = await generateCode();
        const newUserId = await generateUserID();
        const lobby = {
            lobbyCode: newLobbyCode,
            players: [userData.username],
            playerIds: [newUserId]
        };
        stompClient.send('/app/lobby/create', {}, JSON.stringify(lobby));
        stompClient.subscribe('/user/' + newLobbyCode + '/private', onMessageReceived);
        setUserData({...userData, "connected": true, "userId": newUserId, "lobbyCode": newLobbyCode})
    }

    const onJoin = async () => {
        const newUserId = await generateUserID();
        const lobby = {
            lobbyCode: userData.lobbyCode,
            players: [userData.username],
            playerIds: [newUserId]
        };
        stompClient.send('/app/lobby/join', {}, JSON.stringify(lobby));
        stompClient.subscribe('/user/' + userData.lobbyCode + '/private', onMessageReceived);
        setUserData({...userData, "connected": true, "userId": newUserId})
    }

    const joinGame = () => {
        connect();
        stompClient.connect({},onJoin, onError);
    }

    const createGame =()=> {
        connect();
        stompClient.connect({},onCreate, onError);
    }

    const generateCode = () => {
        return new Promise((resolve) => {
            const min = 100000;
            const max = 999999;
            const randomNumber = Math.floor(Math.random() * (max - min + 1)) + min;
            const codeAsString = randomNumber.toString(); // Convert the number to a string
            resolve(codeAsString);
        });
    };

    const generateUserID = () => {
        return new Promise((resolve) => {
            const userId = 'user-' + Math.random().toString(36).substr(2, 9);
            resolve(userId);
        });
    };

    const onError = (err) => {
        console.log(err);
    }

    const onMessageReceived = (payload) => {
        console.log(payload)
    }

    const updateGame = () => {
        const lobby = {
            lobbyCode: userData.lobbyCode,
            answers: [[1,2,3]]
        }
        stompClient.send('/app/lobby/update', {}, JSON.stringify(lobby));
    }

    const createSections = () => {
        return predefinedQuestions.map((question, index) => (
            <div key={index} className="question-section">
                {/* Display predefined question */}
                <p>{question}</p>

                {/* Hint buttons */}
                <button className="hint-button">Hint 1</button>
                <button className="hint-button">Hint 2</button>

                {/* Drop-downs */}
                <select>
                    <option>Option 1</option>
                    <option>Option 2</option>
                </select>
                <select>
                    <option>Option 3</option>
                    <option>Option 4</option>
                </select>
                <select>
                    <option>Option 5</option>
                    <option>Option 6</option>
                </select>
                <button className="check-button">Check</button>
            </div>
        ));
    };


    return (
        <div className ="container">
            {userData.connected?
            <div className = "lobby">
                <h1>Title</h1>

                <div className="lobbycode-section">
                    <p>Your lobby code is: {userData.lobbyCode}</p>
                </div>

                <div className="instructions-section">
                    {/* Space for writing game instructions */}
                    <textarea placeholder="Write game instructions"></textarea>
                </div>

                <div className="joinedUsers-section">
                    <p>Your team: [{lobbyMembers.join(', ')}]</p>
                </div>
                {/* Render 10 sections with predefined questions */}
                {createSections()}
            </div>
            :
            <div className = "login">


                <div id="main">
                    <div id="title-block">
                        <h1>The Crooked Pub</h1>
                    </div>
                    <div id="button-block">
                        <input
                            type="text"
                            placeholder="Enter your name"
                            value={userData.username}
                            onChange={(e) => setUserData({...userData, "username": e.target.value}) }
                        />
                        <input
                            type="text"
                            placeholder="Enter lobby code"
                            value={userData.lobbyCode}
                            onChange={(e) => setUserData({...userData, "lobbyCode": e.target.value})}
                        />
                        <button type="button" onClick={createGame}>
                            Create New Game
                        </button>
                        <button type="button" onClick={joinGame}>
                            Join Game
                        </button>
                    </div>

                    <div id="how-to-play">
                        <p>How to play:</p>
                        <p>Instructions...</p>
                    </div>
                </div>
            </div>
            }
        </div> 
  )
}

export default Lobby
